import ChartRow from './ChartRow';


function ChartLastitem(props){

   // console.log(Object.values(props.product_brand));
    

    return (
                <tr>
                    <td>{props.id}</td>
                    <td>{props.name}</td> 
                    <td>{props.description}</td>
            
                    {/* <td>{props.product_category}</td>   */}
                </tr>
            )
}
    

function LastProductInDb(){

    const [product, setProduct] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
		// Petici贸n Asincr贸nica al montarse el componente
		const endpoint = '/api/products/';

		fetch(endpoint)
				.then(response => response.json())
				.then(data => {	
 					if (!data.Error) {
						setProduct(data.products);      //traigo las categorias
                        setIsLoading(false)
					} 
				})
				.catch(error => console.log(error));		
	}, []);

    useEffect(() => {
	
	}, [product]);

    if (isLoading) {
        return (
          <div className="App">
            <h1>Cargando...</h1>
          </div>
        );
