import React, { useState, useEffect, useRef } from 'react';

// import noPoster from '../assets/images/no-poster.jpg';

function SearchMovies() {
	// const [nombre_del_estado, funcion_actualizacion_del_estado] = useState(estado_inicial);
	const [users, setUsers] = useState([]); // Almacenar y modificar estados / variables.
	const [keyword, setKeyword] = useState('');

	const inputKeyword = useRef();

	useEffect(() => {
		// Petición Asincrónica al montarse el componente
		const endpoint = '/api/users/';

		console.log('El componente se monto')

		fetch(endpoint)
				.then(response => response.json())
				.then(data => {
					console.log('hola');
					console.log(data);
/* 					if (!data.Error) {
						setUsers(data);
						setUsers([]);
					} */
				})
				.catch(error => console.log(error));
		
	}, []);


	const searchMovie = async event => {
		event.preventDefault();
		const inputValue = inputKeyword.current.value;
		setKeyword(inputValue);
		inputKeyword.current.value = '';
	};

	return(
		<div className="container-fluid">
				<>
					<div className="row my-4">
						<div className="col-12 col-md-6">
							{/* Buscador */}
							<form method="GET" onSubmit={searchMovie}>
								<div className="form-group">
									<label htmlFor="">Buscar por título:</label>
									<input ref={inputKeyword} type="text" className="form-control" />
								</div>
								<button className="btn btn-info">Search</button>
							</form>
						</div>
					</div>
					<div className="row">
						<div className="col-12">
							<h2>Películas para la palabra: {keyword}</h2>
						</div>
						{/* Listado de películas */}
						{
							users.length > 0 && users.map((user, i) => {
								return (
									<div className="col-sm-6 col-md-3 my-4" key={i}>
										<div className="card shadow mb-4">
											<div className="card-header py-3">
												<h5 className="m-0 font-weight-bold text-gray-800">{user.lastname}</h5>
											</div>
											<div className="card-body">

	
											</div>
										</div>
									</div>
								)
							})
						}
					</div>
				
				</>
	
				<div className="alert alert-danger text-center my-4 fs-2">Eyyyy... ¿PUSISTE TU APIKEY??</div>

		</div>
	)
}

export default SearchMovies;
