import React, { useState, useEffect, useRef } from 'react';
import SmallCard from './SmallCard';

/*  Cada set de datos es un objeto literal */

/* <!-- Movies in DB --> */




/* <!-- Total awards --> */

let totalAwards = {
    title:' Total awards', 
    color:'success', 
    cuantity: '79',
    icon:'fa-award'
}

/* <!-- Actors quantity --> */

let actorsQuantity = {
    title:'Actors quantity' ,
    color:'warning',
    cuantity:'49',
    icon:'fa-user-check'
}

let cartProps = [totalUsers, totalAwards, actorsQuantity];

function ContentRowMovies(){

    const [users, setUsers] = useState([]); // Almacenar y modificar estados / variables.
    
	const inputKeyword = useRef();

    let totalUsers = {
        title: 'Total de usuarios',
        color: 'primary', 
        cuantity: users.count,
        icon: 'fa-clipboard-list'
    }

	useEffect(() => {
		// Petici贸n Asincr贸nica al montarse el componente
		const endpoint = '/api/users/';

		fetch(endpoint)
				.then(response => response.json())
				.then(data => {
					console.log(data.users);
 					if (!data.Error) {
						setUsers(data.meta);

					} 
				})
				.catch(error => console.log(error));		
	}, []);


    return (
    
        <div className="row">
            
            {cartProps.map( (movie, i) => {

                return <SmallCard {...movie} key={i}/>
            
            })}

        </div>
    )
}

export default ContentRowMovies;
